import 'bootstrap/dist/css/bootstrap.css';
import './RegisterStyle.css'
import { useFormik } from 'formik';
import * as yup from 'yup'
import axios from 'axios';
import { useState } from 'react';


const PASS_REGEX = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/

const validationSchema = yup.object({
    username: yup.string().min(3, "Please enter your real name").required('Username is required'),
    email: yup.string().email("Please enter a valid email").required("Email is required"),
    password: yup.string().matches(PASS_REGEX, "Please enter a strong password").required("Password is required"),
    confirm_password: yup.string().oneOf([yup.ref('password')], "Password does not match")
})


const RegisterForm = () => {
    const [success, setSuccess] = useState(null)
    const [error, setError] = useState(null)

    const onSubmit = async (values) => {
        const { confirm_password, ...data } = values //destructure the values to exclude confirm_password , we dont need to send it to the API
        const response = await axios.post('http://localhost:8080/users/register', data).catch((err) => {
            if (err && err.response) {
                // console.log("Error: ", err.response.message)
                setError()
            }

        })
        if (response && response.data) {
            setSuccess(response.data.message)
            formik.resetForm()
        }
    }

    const formik = useFormik({
        initialValues: { username: '', email: '', password: '', confirm_password: '' },
        validateOnBlur: true,
        onSubmit,
        validationSchema: validationSchema
    })



    return (
        <div className="form-body">
            <div className="row">
                <div className="form-holder">
                    <div className="form-content">
                        <div className="form-items">
                            <h3>Register Today</h3>
                            <p>Fill in the data below.</p>
                            <span className='success'>{success ? success : ""}</span>
                            <form
                                className="requires-validation"
                                onSubmit={formik.handleSubmit}
                                noValidate
                            >
                                <div className="col-md-12">
                                    <input onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.username} className="form-control" type="text" name="username" placeholder="Username" required />
                                    <span className={formik.touched.username && formik.errors.username ? "invalid-feedback" : "valid-feedback"}>{formik.touched.username && formik.errors.username ? formik.errors.username : ""}</span>
                                    {/* <span className={valid ? "valid-feedback" : "invalid-feedback"}>{valid ? "Username field is valid!" : "Username field cannot be blank"}</span> */}
                                    {/* <span className={invalid ? "invalid-feedback" : ""}>Username field cannot be blank!</span> */}
                                </div>
                                <div className="col-md-12">
                                    <input onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.email} className="form-control" type="email" name="email" placeholder="E-mail Address" required />
                                    <span className={formik.touched.email && formik.errors.email ? "invalid-feedback" : "valid-feedback"}>{formik.touched.email && formik.errors.email ? formik.errors.email : ""}</span>
                                    {/* <div className={valid ? "valid-feedback" : "invalid-feedback"}>{valid ? "Email field is valid!" : "Email field cannot be blank!"}</div> */}
                                    {/* <div className={invalid ? "invalid-feedback" : ""}></div> */}
                                </div>
                                {/* <div className="col-md-12">
                                    <select className="form-select mt-3" required>
                                        <option selected disabled value="">Position</option>
                                        <option value="jweb">Junior Web Developer</option>
                                        <option value="sweb">Senior Web Developer</option>
                                        <option value="pmanager">Project Manager</option>
                                    </select>
                                    <div className="valid-feedback">You selected a position!</div>
                                    <div className="invalid-feedback">Please select a position!</div>
                                </div> */}
                                <div className="col-md-12">
                                    <input onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.password} className="form-control" type="password" name="password" placeholder="Password" required />
                                    <span className={formik.touched.password && formik.errors.password ? "invalid-feedback" : "valid-feedback"}>{formik.touched.password && formik.errors.password ? formik.errors.password : ""}</span>
                                    {/* <div className={valid ? "valid-feedback" : "invalid-feedback"}>{valid ? "Password field is valid!" : "Password field cannot be blank!"}</div> */}
                                    {/* <div className={invalid ? "invalid-feedback" : ""}>Password field cannot be blank!</div> */}
                                </div>
                                <div className="col-md-12">
                                    <input onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.confirm_password} className="form-control" type="password" name="confirm_password" placeholder="confirm Password" required />
                                    <span className={formik.touched.confirm_password && formik.errors.confirm_password ? "invalid-feedback" : "valid-feedback"}>{formik.touched.confirm_password && formik.errors.confirm_password ? formik.errors.confirm_password : ""}</span>

                                    {/* <div className={valid ? "valid-feedback" : "invalid-feedback"}>{valid ? "Confirm Password field is valid!" : "Confirm Password field cannot be blank!"}</div> */}
                                    {/* <div className={invalid ? "invalid-feedback" : ""}>Confirm Password field cannot be blank!</div> */}
                                </div>
                                {/* <div className="col-md-12 mt-3">
                                    <label className="mb-3 mr-1" for="gender">Gender: </label>
                                    <input type="radio" className="btn-check" name="gender" id="male" autocomplete="off" required />
                                    <label className="btn btn-sm btn-outline-secondary" for="male">Male</label>
                                    <input type="radio" className="btn-check" name="gender" id="female" autocomplete="off" required />
                                    <label className="btn btn-sm btn-outline-secondary" for="female">Female</label>
                                    <input type="radio" className="btn-check" name="gender" id="secret" autocomplete="off" required />
                                    <label className="btn btn-sm btn-outline-secondary" for="secret">Secret</label>
                                    <div className="valid-feedback mv-up">You selected a gender!</div>
                                    <div className="invalid-feedback mv-up">Please select a gender!</div>
                                </div> */}

                                <div className="form-button mt-3">
                                    <button id="submit" type="submit" className="btn btn-primary">Register</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default RegisterForm
