// import React from 'react'
import { useLocation } from 'react-router-dom'
import React, { useEffect, useState } from 'react'
import axios from 'axios'


const Profile = (props) => {
    // const id = props.id
    const location = useLocation()
    const [userName, setUserName] = useState(null);
    const [userEmail, setUserEmail] = useState(null);
    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/profile/${location.state.id}`, {
                    headers: {
                        Authorization: 'bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IsY0Yjk4ZTBlYzQ1MzEyMjAyYmNjYzA5NiIsImlhdCI6MTY4OTg4MjEyNn0.DTgCOrkWloAm6tQv2mYeb-Ns4bXZVloNjXOQjUoEqtU',

                    }
                }).catch((error) => {
                    console.log(response.data.error)

                });
                setUserName(response.data.user.username);
                setUserEmail(response.data.user.email);
                // console.log(response.data.data.username)
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        fetchUserData();
    })
    return (
        <>
            <h1 style={{ color: "white" }}>
                username {userName}
            </h1>

            <h1 style={{ color: "white" }}>
                email {userEmail}
            </h1></>
    )
}

export default Profile
