    const location = useLocation()

    // let userName = location.state.username
    // let role = location.state.role

    // let userName = location.state ? location.state.username : null;`
    // let role = location.state ? location.state.role : null;`

    const userName = localStorage.getItem('username')
    const role = localStorage.getItem('role')

    const [users, setUsers] = useState([])
    const [username, setUserame] = useState('')
    const [Role, setRole] = useState('')

    useEffect(() => {


        const fetchUserData = async () => {
            try {
                // console.log("0 ")
                const response = await axios.post(`http://localhost:8080/dashboard`, {
                    role: role
                    // username:username
                    // headers: {
                    //     Authorization: 'bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY0YjlhM2Y3Yjg0MWNlMDA5OTBkNzkxNCIsImlhdCI6MTY4OTg4NzczNX0.2jnvpkseQT8tzqLt1t07H5DjraflURUoH6QIufpvTNA',
                    // },

                }).catch((err) => {
                    if (err && err.response) {
                        console.log("Error: ", err.response.data.error)
                        navigate('/login'); //redirect to the profile page
                    }
                });
                if (response && response.data) {
                    setUsers(response.data.data.users)
                    setUserame(userName)
                    setRole(role)
                    // console.log("userName ",userName)
                    // console.log(response.data.data.users)
                }
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
    }, [])



    let navigate = useNavigate();
    const logout = () => {
        localStorage.clear();
        localStorage.setItem('logout', true)
        navigate('/login');
    }
    const x=localStorage.getItem
