import axios from 'axios';
import './App.css'
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css'

function App() {
  const [dataToServer, setDataServer] = useState('');

  const sendData = async () => {
    try {
      await axios.post('http://127.0.0.1:8080', { tweet: dataToServer });
      setDataServer('')
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App container">
      <div className='main_div'>
        <button id='btn' onClick={sendData}>Twit</button>
        <input id='input' value={dataToServer} type="text" onChange={e => setDataServer(e.target.value)} />
      </div>
    </div>



    // <div class="tweet-container">
