import axios from 'axios';
import './App.css'
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css'
import profile_img from './profile.png'

function App() {
  const [dataToServer, setDataServer] = useState('');

  const sendData = async () => {
    try {
      await axios.post('http://127.0.0.1:8080', { tweet: dataToServer });
      setDataServer('')
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <div className="tweet-container">
        <div className="d-flex gap-2">
          <img alt='' src={profile_img} style={{ borderRadius: '50%', width: "50px", height: "50px" }}></img>
          <textarea onChange={e => setDataServer(e.target.value)} value={dataToServer} placeholder="What's happening?"></textarea>
        </div>
        <div className='actions'>
          <button id='btn' onClick={sendData}>Twit</button>
          <span class="char-count">140</span>
        </div>
        <hr />
        <h3>My Tweets</h3>

      </div>
    </div>
  );
}

export default App;
